import { createImage, checkTaskStatus } from "../src/image.js";

const test = async () => {
  const resp = await createImage({ prompt: "一只奔跑的加菲猫" });
  if (!resp) return;
  const output = resp.output;
  if (output.taskId) {
    const result = await checkTaskStatus(output.taskId);
    console.log(result);
  }
};

test();
